mapper.select:
SELECT p.product_id AS productId, p.product_name AS productName, p.price, p.category, p.stock, p.description, p.created_at AS createdAt, p.updated_at AS updatedAt, pi.image_path AS mainImage FROM product p LEFT JOIN product_image pi ON p.product_id = pi.product_id AND pi.sort_order = 0
category = #{category}
price >= #{priceMin}
price <= #{priceMax}
ORDER BY p.created_at DESC
